import React, { useState } from 'react';

const ThemeSwitcher = () => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    console.log('clicked');
    setDarkMode(darkMode);
  };

  return (
    <button onClick={toggleDarkMode} className="block text-sm text-gray-500 dark:bg-slate-950 hover:text-indigo-500 hover:bg-slate-950 duration-500 md:p-4 py-7 px-10 rounded w-full   font-urbanist  md:ml-0">
      <ion-icon name="contrast-outline"></ion-icon>
    </button>
  );
};

export default ThemeSwitcher;
