function MeetupItem(props) {
  return (
    <li className="bg-white rounded-b-lg w-fit overflow-hidden drop-shadow-2xl">
      <div className=" h-56  object-cover">
        <img src={props.image} alt={props.title} width={400} />
      </div>
      {/* <div className="p-7">
        <h3 className="font-bold text-slate-950 text-2xl my-1">{props.title}</h3>
        <address className="text-gray-600 text-xl">{props.address}</address>
        <p className="text-gray-600 text-base">{props.description}</p>
      </div> */}
      <div className="bg-blue-600 text-white font-bold absolute top-0 m-4 px-3 py-1 rounded-sm">
        <span> {props.rating}</span>
      </div>
      <button className="block text-xl  text-gray-500 dark:bg-slate-950 hover:text-indigo-500 hover:bg-slate-950 duration-500 md:p-4 py-7 px-10 rounded-b-lg w-full   font-urbanist">
        To Favorites
      </button>
    </li>
  );
}

export default MeetupItem;
