import React, { useState } from 'react';

const ThemeSwitcher = () => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <button onClick={toggleDarkMode} className="theme-switcher">
      Toggle Dark Mode
    </button>
  );
};

export default ThemeSwitcher;
