import { useState ,useEffect} from "react";

import MeetupList from "../components/meetups/MeetupsList";

const Dummy_Data = [
  {
    id: "m1",
    title: "League of Legeniggers",
    image:
      "https://m.media-amazon.com/images/I/91WNKEU1HKL._SX1200_._FMwebp_.jpg",
    address: "Niggers rift",
    description: "LOOK AT THE CLEANSE LOOK AT THE MOVES",
    rating: "11/10",
  },
  {
    id: "m2",
    title: "Kounter Strike Russian Offensive",
    image:
      "https://dotesports.com/wp-content/uploads/2023/06/Counter-Strike-2-e1686091171756.jpg",
    address: "Office, office , long match office",
    description: "Zarooba zarooba , karashov nigger karashov",
    rating: "Karashov/10",
  },
  {
    id: "m3",
    title: "MoineKraft",
    image:
      "https://assets.nintendo.com/image/upload/ar_16:9,c_lpad,w_1240/b_white/f_auto/q_auto/ncom/software/switch/70010000000964/811461b8d1cacf1f2da791b478dccfe2a55457780364c3d5a95fbfcdd4c3086f",
    address: "plain planks",
    description: "Creeeeeepppperrr OOOOH MAAAAN",
    rating: "Creeper/Ohh man",
  },
];

function AllMeetupsPage() {
  const [isLoading, setIsLoading] = useState(true);
  const [loadedMeetups,setLoadedMeetups]=useState([]);

  useEffect(()=>{
    setIsLoading(true);
    fetch(
      "https://react-refresher-65646-default-rtdb.europe-west1.firebasedatabase.app/meetups.json"
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const meetups=[];
        for (const key in data ){
          const meetupp={
            id:key,
            // title:data[key].title,
            // image:data[key].image,
            // address:data[key].address,
            // description:data[key].description,
            ...data[key]
          }
        }
        setIsLoading(false);
        setLoadedMeetups(data);
      });
  }, []);
  

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="dark:bg-slate-950 bg-indigo-300 w-full dark:text-white text-black font-urbanist">
      <h1 className="text-3xl text-center my-10"> All Meetups Available</h1>
      <MeetupList meetups={loadedMeetups} />

      {/* <ul>
      {Dummy_Data.map( (meet) => {
        return <li key={meet.id}>
          {meet.title}
        </li>
      })}
      </ul> */}
    </div>
  );
}

export default AllMeetupsPage;
