import { Link } from "react-router-dom";
import SButton from "./SButton";
import { useState } from "react";

let Links = [
  {
    name: "All Meetups",
    path: "/",
  },
  {
    name: "New Meetups",
    path: "/new-meetup",
  },
  {
    name: "My Favourites",
    path: "/favs",
  },
];

function MainNav() {
    let [open, setOpen] = useState(false);

  return (
    <div className="shadow-md w-full fixed top-0 left-0">
      <div className="md:flex items-center justify-between bg-white py-4 md:px-10 px-7">
        <Link to="/">
          <div
            className="font-bold text-2xl cursor-pointer flex items-center font-urbanist
        text-gray-800"
          >
            <span className="text-3xl text-indigo-600 mr-1 pt-2">
              <ion-icon name="game-controller-outline"></ion-icon>
            </span>
            GameHub
          </div>
        </Link>
        <div onClick={() => setOpen(!open)}
          className="text-3xl absolute right-8 top-6 cursor-pointer
         md:hidden "
        >
          <ion-icon name={open ? 'close-circle-outline': 'menu-outline'}></ion-icon>
          <ion-icon name="menu-outline"></ion-icon>
          <ion-icon name="close-circle-outline"></ion-icon>
        </div>
        <ul
          className=" md:flex md:items-center md:pb-0 pb-12 absolute md:static
        bg-white md:z-auto z-[-1] left-0 w-full md:w-auto md:pl-0 pl-0 transition-all duration-500 ease-in"
        >
          {Links.map((link) => (
            <li key={link.name} className="md:ml-8 text-xl md:my-0 my-7">
              <Link
                to={link.path}
                className="text-gray-800 hover:text-gray-400 duration-500"
              >
                {link.name}
              </Link>
            </li>
          ))}
          {/* <li>
            <Link to="/">All Meetups</Link>
          </li>
          <li>
            <Link to="/new-meetup">New Meetups</Link>
          </li>
          <li>
            <Link to="/favs"> My Favourites</Link>
          </li> */}
          <SButton text={"Signup!"} />
        </ul>
      </div>
    </div>
  );
}

export default MainNav;
