import { Link } from "react-router-dom";
import SButton from "./SButton";
import { useState } from "react";
import classNames from "classnames";

let Links = [
  {
    name: " All Meetups",
    path: "/",
  },
  {
    name: "New Meetups",
    path: "/new-meetup",
  },
  {
    name: "My Favourites",
    path: "/favs",
  },
];

function MainNav() {
  let [open, setOpen] = useState(false);
  const classname = classNames(
    "flex",
    "md:flex-row",
    "flex-col",
    "ml-1",
    "items-center",
    "md:pb-0",
    "pb-12",
    "md:static",
    "bg-white",
    "md:z-auto",
    "z-8",
    "left-0",
    "w-full",
    "md:w-auto",
    "md:pl-0",
    "pl-0",
    "transition-all",
    "duration-500",
    "ease-in",
    "dark:bg-slate-950",
    `${open ? "static " : "hidden md:flex"}`
  );
  return (
    <div className="shadow-md w-full relative top-0 left-0">
      <div className="md:flex items-center justify-between dark:bg-slate-950 bg-white py-4 md:px-10 px-7">
        <Link to="/">
          <div
            className=" dark:text-indigo-600 font-bold text-2xl cursor-pointer flex items-center font-urbanist
        text-gray-800"
          >
            <span className="text-3xl text-indigo-600 mr-1 pt-2">
              <ion-icon name="game-controller-outline"></ion-icon>
            </span>
            GameHub
          </div>
        </Link>
        <div
          onClick={() => setOpen(!open)}
          className="text-3xl text-indigo-600 absolute right-8 top-6 cursor-pointer
         md:hidden "
        >
          <ion-icon
            name={open ? "close-circle-outline" : "menu-outline"}
          ></ion-icon>
        </div>
        <div className="items-center">
          <ul className={classname}>
            {Links.map((link) => (
              <li key={link.name} className="md:ml-0  text-xl md:my-0 my-7 ">
                <Link
                  to={link.path}
                  className="box-border block text-gray-500 dark:bg-slate-950 hover:text-indigo-500 hover:bg-slate-950 duration-500 lg:py-4 px-10 rounded w-full "
                >
                  {link.name}
                </Link>
              </li>
            ))}
            {/* <li>
            <Link to="/">All Meetups</Link>
          </li>
          <li>
            <Link to="/new-meetup">New Meetups</Link>
          </li>
          <li>
            <Link to="/favs"> My Favourites</Link>
          </li> */}
            <SButton text={"Signup!"} />
          </ul>
        </div>
      </div>
    </div>
  );
}

export default MainNav;
