import Modal from "./Modal";
import Backdrop from "./Backdrop";

function Todo(props) {
  useState(false);

  const [modalIsOpen, setModalIsOpen] = useState(false);

  function DeleteHandler() {
    console.log("clicked");
    console.log(props.text);
    setModalIsOpen(true);
  }
  function CloseModal() {
    setModalIsOpen(false);
  }
  return (
    <div>
      <div className="bg-white rounded w-96 h-28 overflow-hidden drop-shadow-2xl">
        <h2 className="text-3xl p-6 pl-8">{props.text}</h2>
        <div>
          <button
            className=" btn-del absolute bottom-0 right-0"
            onClick={DeleteHandler}
          >
            Delete
          </button>
        </div>
      </div>
      {
        modalIsOpen ? <Modal onCancel={CloseModal1} onConfirm={CloseModal}/> : null
      }
      {
        modalIsOpen ? <Backdrop onClick={CloseModal}/> : null
      }

    </div>
  );
}

export default Todo;
