import NMForm from "../components/meetups/NMForm";
import { useHistory } from  'react-router-dom';

function NewMeetupsPage() {
  const history = useHistory();

  function addMeetupHandler(meetupData) {
    console.log(meetupData);
    fetch(
      "https://react-refresher-65646-default-rtdb.europe-west1.firebasedatabase.app/meetups.json"
      ,{
        method: 'POST',
        body: JSON.stringify(meetupData),
        headers: {
                  'Content-Type': 'application/json'
                }
      }
      ).then(()=>{
        history.replace('/');
      });
  }
  return (
    <div className=" dark:bg-slate-950 h-screen">
      <h1 className="text-3xl dark:text-white text-black text-center my-10">Add New Meetup</h1>
      <NMForm onAdd={addMeetupHandler} />
    </div>
  );
}

export default NewMeetupsPage;
